mapper.select:
SELECT a.ID, a.ORDER_ID, a.ACCOUNT, c.cmer as MERCHANT_NAME, CONCAT(SUBSTR(c.real_name,1,1) ,'**') as realName, a.AMOUNT, a.TOTAL_PROFIT, a.AGENT_ID, b.full_name as AGENT_NAME, b.account AS AGENT_ACCOUNT, a.AGENT_PROFIT, a.TJ_PROFIT, a.TJ_ID, a.ORDER_DATE, a.JS_DATE, a.CREATE_DATE, c.cmer_sort as TJ_NAME FROM fm_profit a LEFT JOIN fm_user b ON a.agent_id = b.id LEFT JOIN fm_merchant c ON c.account = a.account AND c.tj_id=a.tj_id
and a.ID = #{id}
and a.ORDER_ID = #{orderId}
and a.ACCOUNT = #{account}
and a.AMOUNT = #{amount}
and a.TOTAL_PROFIT = #{totalProfit}
and a.AGENT_ID = #{agentId}
and a.AGENT_PROFIT = #{agentProfit}
and a.TJ_PROFIT = #{tjProfit}
and a.TJ_ID = #{tjId}
and DATE_FORMAT(a.ORDER_DATE, '%Y-%m-%d') = DATE_FORMAT(#{orderDate}, '%Y-%m-%d')
and a.JS_DATE = #{jsDate}
and a.CREATE_DATE = #{createDate}

select
ID,ORDER_ID,ACCOUNT,AMOUNT,TOTAL_PROFIT,AGENT_ID,AGENT_PROFIT,TJ_PROFIT,TJ_ID,ORDER_DATE,JS_DATE,CREATE_DATE
from fm_profit


		and ID = #{id}


		and ORDER_ID = #{orderId}


		and ACCOUNT = #{account}


		and AMOUNT = #{amount}


		and TOTAL_PROFIT = #{totalProfit}


		and AGENT_ID = #{agentId}


		and AGENT_PROFIT = #{agentProfit}


		and TJ_PROFIT = #{tjProfit}


		and TJ_ID = #{tjId}


		and DATE_FORMAT(ORDER_DATE, '%Y-%m-%d') =
		DATE_FORMAT(#{orderDate}, '%Y-%m-%d')


		and JS_DATE = #{jsDate}


		and CREATE_DATE = #{createDate}


		and STATUS = #{status}

SELECT a.agent_id, b.full_name, b.account, SUM(a.amount) as total_amount, SUM(a.agent_profit) as total_profit, a.order_date as date FROM fm_profit a LEFT JOIN fm_user b ON a.agent_id = b.id
b.account = #{account}
GROUP BY DATE_FORMAT(a.order_date, '%Y-%m-%d')
